import 'dart:async';import 'package:chatbot_allen/constants/apiKeys.dart';import 'package:google_generative_ai/google_generative_ai.dart';class GeminiAIService{  Future<String> textPromptResult(String prompt)async{    try{      final model = GenerativeModel(model: 'gemini-1.5-flash', apiKey: GeminiAIapiKey);      final content = [Content.text(prompt)];      final response = await model.generateContent(content);      return response.text.toString();    }catch(e){      return e.toString();    }  }  Future<Stream<GenerateContentResponse>> StreamResponse(String prompt)async{    try{      final model = GenerativeModel(model: 'gemini-1.5-flash', apiKey: GeminiAIapiKey);      final content = [Content.text(prompt)];      Stream<GenerateContentResponse> response = model.generateContentStream(content);      return response;    }catch(e){      throw e.toString();    }  }}