import 'package:flutter/cupertino.dart';class speechTextProvider extends ChangeNotifier{  String _text = '';  String get text => _text;  void updateText(String value){    _text = value;    notifyListeners();  }  bool? _boolean;  bool? get isArtPromptValue => _boolean;  void IsArtPrompt(bool? value){    _boolean = value;    notifyListeners();  }  List<String> _promptArray = [];  List<String> get promptArray => _promptArray;  void setPromptArray(List<String> value){    _promptArray = value;    notifyListeners();  }}