import 'package:flutter/cupertino.dart';class speechTextProvider extends ChangeNotifier{  String _text = '';  String get text => _text;  void updateText(String value){    _text = value;    notifyListeners();  }  List<String> _promptArray = [];  List<String> get promptArray => _promptArray;  void setPromptArray(List<String> value){    _promptArray = value;    notifyListeners();  }  List<String> _text2 = [];  List<String> get streamData => _text2;  void StreamData(List<String> value){    _text2 = value;    notifyListeners();  }}