import 'package:chatbot_allen/respository/gemini_services.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class ChatHistoryList extends StatefulWidget {  final String spokenWords;  const ChatHistoryList({super.key,required this.spokenWords});  @override  State<ChatHistoryList> createState() => _ChatHistoryListState();}class _ChatHistoryListState extends State<ChatHistoryList> {  GeminiAIService geminiServices = GeminiAIService();  late String isArtPromptResult;  late bool isArtPrompt;  @override  void initState() {    super.initState();  }  Future<void> isPromptApiCall()async{    isArtPromptResult = await geminiServices.isArtPromptAPI(widget.spokenWords);    isArtPrompt = await isPromptArt(isArtPromptResult);  }  bool isPromptArt(String result){    switch(result){      case 'Yes':        return true;      case 'yes':        return true;      case 'Yes.':        return true;      case 'yes.':        return true;      case 'YES':        return true;      case 'NO':        return false;      case 'No':        return false;      case 'no':        return false;      case 'No.':        return false;      case 'no.':        return false;      default:        return false;    }  }  @override  Widget build(BuildContext context) {    isPromptApiCall();    print(isArtPrompt.toString()+" Value from GEMINI");    return Container(      color: Colors.blue,      child: ListView.builder(itemBuilder: (context,index){        return Container();      }),    );  }}