import 'package:chatbot_allen/constants/color_pallet.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';class FeatureBox extends StatelessWidget {  final String title;  final Color boxColor;  final String descriptionText;  FeatureBox({    required this.title,    required this.boxColor,    required this.descriptionText,  });  @override  Widget build(BuildContext context) {    return Container(      margin:const EdgeInsets.symmetric(horizontal: 25,vertical: 10),      padding:const EdgeInsets.symmetric(horizontal: 15,vertical: 10),      decoration: BoxDecoration(        color: boxColor,        borderRadius:const BorderRadius.all(Radius.circular(12))      ),      child: Column(        mainAxisAlignment: MainAxisAlignment.start,        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Text(            title,            style:const TextStyle(              fontSize: 19,              fontFamily: 'Cera Pro',              fontWeight: FontWeight.bold,              color: Pallet.mainFontColor,            ),          ),          Text(            descriptionText,            style:GoogleFonts.openSans(              textStyle: TextStyle(                fontSize: 14,                fontWeight: FontWeight.normal,                color: Colors.black87,              ),            )          )        ],      ),    );  }}