import 'package:chatbot_allen/constants/color_pallet.dart';import 'package:chatbot_allen/respository/gemini_services.dart';import 'package:chatbot_allen/speechProvider/spech_text_provider.dart';import 'package:chatbot_allen/view/intro_containers.dart';import 'package:chatbot_allen/view/search_section.dart';import 'package:flutter/material.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:provider/provider.dart';import 'package:speech_to_text/speech_to_text.dart';import 'ChatHistoryList.dart';class HomePage extends StatefulWidget {  @override  State<HomePage> createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  SpeechToText textToSpeech = SpeechToText();  GeminiAIService openAIService = GeminiAIService();  @override  void initState() {    super.initState();  }  @override  void dispose() {    super.dispose();    textToSpeech.stop();  }  @override  Widget build(BuildContext context) {    String spokenWords = Provider.of<speechTextProvider>(context).text;    print('Spoken Words @@@ $spokenWords @@@');    return Scaffold(  //Main AppBar of application      appBar: AppBar(        backgroundColor: Colors.white,        centerTitle: true,        surfaceTintColor: Colors.white,        title: const Text(          'ChatBot-Allen',          style: TextStyle(            fontSize: 19,            fontFamily: 'Cera Pro',            color: Colors.black,          ),        ),        leading: const Icon(Icons.menu),      ),      //Main Body Content of HomePage      body: Container(        color: Colors.white,        height: MediaQuery.of(context).size.height,        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceBetween,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            //Content Part            Expanded(              child: SingleChildScrollView(                  child: Column(                mainAxisAlignment: MainAxisAlignment.start,                crossAxisAlignment: CrossAxisAlignment.center,                children: [                  //ChatBot-Allen Logo SVG                  Center(                    child: Container(                        margin: const EdgeInsets.only(top: 5),                        width: 120,                        height: 120,                        padding: const EdgeInsets.all(20),                        decoration: const BoxDecoration(                          shape: BoxShape.circle,                          color: Pallet.assistantCircleColor,                        ),                        child: SvgPicture.asset(                          'assets/images/robot.svg',                        )),                  ),                  //Intro-Containers for first time                  spokenWords==''?IntroContainer():ChatHistoryList(spokenWords: spokenWords,)                ],              )),            ),            //Search Part            const SearchSection()          ],        ),      ),    );  }}